import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { SidebarProvider, SidebarTrigger } from "@/components/ui/sidebar";
import { AppSidebar } from "@/components/app-sidebar";
import { ThemeToggle } from "@/components/theme-toggle";
import { DemoModeToggle } from "@/components/demo-mode-toggle";
import Dashboard from "@/pages/dashboard";
import Flights from "@/pages/flights";
import Bottles from "@/pages/bottles";
import Trolleys from "@/pages/trolleys";
import Replanning from "@/pages/replanning";
import Rules from "@/pages/rules";
import Orders from "@/pages/orders";
import NotFound from "@/pages/not-found";

function Router() {
  return (
    <Switch>
      <Route path="/" component={Dashboard} />
      <Route path="/flights" component={Flights} />
      <Route path="/bottles" component={Bottles} />
      <Route path="/trolleys" component={Trolleys} />
      <Route path="/replanning" component={Replanning} />
      <Route path="/rules" component={Rules} />
      <Route path="/orders" component={Orders} />
      <Route component={NotFound} />
    </Switch>
  );
}

export default function App() {
  const style = {
    "--sidebar-width": "16rem",
    "--sidebar-width-icon": "3rem",
  };

  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <SidebarProvider style={style as React.CSSProperties}>
          <div className="flex h-screen w-full">
            <AppSidebar />
            <div className="flex flex-col flex-1 overflow-hidden">
              <header className="flex items-center justify-between px-6 py-4 border-b border-border bg-background sticky top-0 z-10">
                <div className="flex items-center gap-4">
                  <SidebarTrigger data-testid="button-sidebar-toggle" />
                  <div className="flex flex-col">
                    <h1 className="text-xl font-bold text-foreground">SmartCater</h1>
                    <p className="text-xs text-muted-foreground">Intelligent Catering Execution for GateGroup</p>
                  </div>
                </div>
                <div className="flex items-center gap-2">
                  <DemoModeToggle />
                  <ThemeToggle />
                </div>
              </header>
              <main className="flex-1 overflow-auto p-6">
                <Router />
              </main>
            </div>
          </div>
        </SidebarProvider>
        <Toaster />
      </TooltipProvider>
    </QueryClientProvider>
  );
}

