import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage, extractAirlineCode } from "./storage";
import { insertFlightSchema, insertAirlineRuleSchema, insertOrderSchema, insertReassignmentSchema } from "@shared/schema";
import { analyzeBottleImage, verifyTrolleyImage } from "./openai";
import { getWeatherData, calculateFlightReliability, getReliabilityRecommendation } from "./weather";

export async function registerRoutes(app: Express): Promise<Server> {
  // Flight routes
  app.get("/api/flights", async (_req, res) => {
    try {
      const flights = await storage.getFlights();
      res.json(flights);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch flights" });
    }
  });

  app.get("/api/flights/:id", async (req, res) => {
    try {
      const flight = await storage.getFlight(req.params.id);
      if (!flight) {
        return res.status(404).json({ error: "Flight not found" });
      }
      res.json(flight);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch flight" });
    }
  });

  app.post("/api/flights", async (req, res) => {
    try {
      const validated = insertFlightSchema.parse(req.body);
      const flight = await storage.createFlight(validated);
      res.status(201).json(flight);
    } catch (error: any) {
      res.status(400).json({ error: error.message || "Invalid flight data" });
    }
  });

  app.post("/api/flights/:id/check-reliability", async (req, res) => {
    try {
      const flight = await storage.getFlight(req.params.id);
      if (!flight) {
        return res.status(404).json({ error: "Flight not found" });
      }

      const weather = await getWeatherData(flight.destination, new Date(flight.departureTime));
      const reliability = calculateFlightReliability(weather);
      const recommendation = getReliabilityRecommendation(reliability);

      const updated = await storage.updateFlight(flight.id, { reliability });

      res.json({
        flight: updated,
        weather,
        reliability,
        recommendation,
      });
    } catch (error: any) {
      res.status(500).json({ error: error.message || "Failed to check reliability" });
    }
  });

  app.patch("/api/flights/:id", async (req, res) => {
    try {
      const updated = await storage.updateFlight(req.params.id, req.body);
      if (!updated) {
        return res.status(404).json({ error: "Flight not found" });
      }
      res.json(updated);
    } catch (error: any) {
      res.status(500).json({ error: error.message || "Failed to update flight" });
    }
  });

  app.delete("/api/flights/:id", async (req, res) => {
    try {
      const deleted = await storage.deleteFlight(req.params.id);
      if (!deleted) {
        return res.status(404).json({ error: "Flight not found" });
      }
      res.json({ success: true });
    } catch (error) {
      res.status(500).json({ error: "Failed to delete flight" });
    }
  });

  // Airline rules routes
  app.get("/api/rules", async (_req, res) => {
    try {
      const rules = await storage.getAirlineRules();
      res.json(rules);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch rules" });
    }
  });

  app.post("/api/rules", async (req, res) => {
    try {
      const validated = insertAirlineRuleSchema.parse(req.body);
      const rule = await storage.createAirlineRule(validated);
      res.status(201).json(rule);
    } catch (error: any) {
      res.status(400).json({ error: error.message || "Invalid rule data" });
    }
  });

  app.patch("/api/rules/:id", async (req, res) => {
    try {
      const updated = await storage.updateAirlineRule(req.params.id, req.body);
      if (!updated) {
        return res.status(404).json({ error: "Rule not found" });
      }
      res.json(updated);
    } catch (error: any) {
      res.status(500).json({ error: error.message || "Failed to update rule" });
    }
  });

  // Bottle analysis routes
  app.get("/api/bottles/recent", async (_req, res) => {
    try {
      const analyses = await storage.getBottleAnalyses();
      res.json(analyses);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch bottle analyses" });
    }
  });

  app.post("/api/bottles/analyze", async (req, res) => {
    try {
      const { flightId, imageData } = req.body;

      if (!flightId || !imageData) {
        return res.status(400).json({ error: "Missing flightId or imageData" });
      }

      const flight = await storage.getFlight(flightId);
      if (!flight) {
        return res.status(404).json({ error: "Flight not found" });
      }

      // Get airline-specific rules or use defaults
      const airlineRule = await storage.getAirlineRule(flight.airline);
      const reuseThreshold = airlineRule?.reuseThreshold || 70;
      const combineThreshold = airlineRule?.combineThreshold || 40;

      // Analyze the image using OpenAI Vision
      const result = await analyzeBottleImage(imageData, reuseThreshold, combineThreshold);

      // Store the analysis
      const analysis = await storage.createBottleAnalysis({
        flightId,
        imageData,
        bottleType: result.bottleType,
        fillLevel: result.fillLevel,
        recommendedAction: result.recommendedAction,
        aiAnalysis: result.analysis,
      });

      res.status(201).json(analysis);
    } catch (error: any) {
      console.error("Bottle analysis error:", error);
      res.status(500).json({ error: error.message || "Failed to analyze bottle" });
    }
  });

  // Trolley verification routes
  app.get("/api/trolleys/recent", async (_req, res) => {
    try {
      const verifications = await storage.getTrolleyVerifications();
      res.json(verifications);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch trolley verifications" });
    }
  });

  app.post("/api/trolleys/verify", async (req, res) => {
    try {
      const { flightId, imageData, goldenLayoutName } = req.body;

      if (!flightId || !imageData) {
        return res.status(400).json({ error: "Missing flightId or imageData" });
      }

      const flight = await storage.getFlight(flightId);
      if (!flight) {
        return res.status(404).json({ error: "Flight not found" });
      }

      // Verify the image using OpenAI Vision
      const result = await verifyTrolleyImage(imageData);

      // Store the verification
      const verification = await storage.createTrolleyVerification({
        flightId,
        imageData,
        goldenLayoutName: goldenLayoutName || "Standard Layout",
        hasErrors: result.hasErrors ? 1 : 0,
        errors: result.errors,
        aiAnalysis: result.analysis,
      });

      res.status(201).json(verification);
    } catch (error: any) {
      console.error("Trolley verification error:", error);
      res.status(500).json({ error: error.message || "Failed to verify trolley" });
    }
  });

  // Metrics routes
  app.get("/api/metrics", async (_req, res) => {
    try {
      const metrics = await storage.getDashboardMetrics();
      res.json(metrics);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch metrics" });
    }
  });

  app.get("/api/metrics/efficiency-trend", async (_req, res) => {
    try {
      const trend = await storage.getEfficiencyTrend();
      res.json(trend);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch efficiency trend" });
    }
  });

  app.get("/api/metrics/food-saved-trend", async (_req, res) => {
    try {
      const trend = await storage.getFoodSavedTrend();
      res.json(trend);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch food saved trend" });
    }
  });

  app.get("/api/metrics/employees", async (_req, res) => {
    try {
      const employees = await storage.getEmployeeMetrics();
      res.json(employees);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch employee metrics" });
    }
  });

  // Customer orders routes
  app.get("/api/orders", async (_req, res) => {
    try {
      const orders = await storage.getOrders();
      res.json(orders);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch orders" });
    }
  });

  app.get("/api/orders/pending", async (_req, res) => {
    try {
      const orders = await storage.getPendingOrders();
      res.json(orders);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch pending orders" });
    }
  });

  app.post("/api/orders", async (req, res) => {
    try {
      const validated = insertOrderSchema.parse(req.body);
      const order = await storage.createOrder(validated);
      res.status(201).json(order);
    } catch (error: any) {
      res.status(400).json({ error: error.message || "Invalid order data" });
    }
  });

  app.post("/api/orders/:id/complete", async (req, res) => {
    try {
      const order = await storage.getOrder(req.params.id);
      if (!order) {
        return res.status(404).json({ error: "Order not found" });
      }

      if (order.status !== "pending") {
        return res.status(400).json({ error: "Order must be pending to complete" });
      }

      const updated = await storage.updateOrderStatus(req.params.id, "in_verification");
      res.json(updated);
    } catch (error: any) {
      res.status(500).json({ error: error.message || "Failed to complete order" });
    }
  });

  app.post("/api/orders/:id/verify", async (req, res) => {
    try {
      const order = await storage.getOrder(req.params.id);
      if (!order) {
        return res.status(404).json({ error: "Order not found" });
      }

      if (order.status !== "in_verification") {
        return res.status(400).json({ error: "Order must be in verification to verify" });
      }

      const updated = await storage.updateOrderStatus(req.params.id, "completed");
      res.json(updated);
    } catch (error: any) {
      res.status(500).json({ error: error.message || "Failed to verify order" });
    }
  });

  app.post("/api/orders/:id/cancel", async (req, res) => {
    try {
      const order = await storage.getOrder(req.params.id);
      if (!order) {
        return res.status(404).json({ error: "Order not found" });
      }

      const updated = await storage.updateOrderStatus(req.params.id, "cancelled");
      res.json(updated);
    } catch (error: any) {
      res.status(500).json({ error: error.message || "Failed to cancel order" });
    }
  });

  app.delete("/api/orders/:id", async (req, res) => {
    try {
      const deleted = await storage.deleteOrder(req.params.id);
      if (!deleted) {
        return res.status(404).json({ error: "Order not found" });
      }
      res.json({ success: true });
    } catch (error) {
      res.status(500).json({ error: "Failed to delete order" });
    }
  });

  // Reassignment routes
  app.get("/api/reassignments", async (_req, res) => {
    try {
      const reassignments = await storage.getReassignments();
      res.json(reassignments);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch reassignments" });
    }
  });

  app.post("/api/reassignments/process", async (_req, res) => {
    try {
      const flights = await storage.getFlights();
      const delayedFlights = flights.filter((f) => f.status === "delayed" || f.status === "cancelled");
      const reassignments = [];

      for (const delayedFlight of delayedFlights) {
        const compatibleFlight = await storage.findCompatibleFlight(delayedFlight, flights);
        const airlineCode = extractAirlineCode(delayedFlight.flightNumber, delayedFlight.airline);

        if (compatibleFlight) {
          const reassignment = await storage.createReassignment({
            fromFlightNumber: delayedFlight.flightNumber,
            toFlightNumber: compatibleFlight.flightNumber,
            airline: delayedFlight.airline,
            airlineCode,
            mealsReassigned: delayedFlight.plannedMeals || 0,
            bottlesReassigned: delayedFlight.plannedBottles || 0,
            status: "success",
            reason: `Reassigned from ${delayedFlight.status} flight ${delayedFlight.flightNumber} to ${compatibleFlight.flightNumber} (same airline)`,
          });
          reassignments.push(reassignment);
        } else {
          const reassignment = await storage.createReassignment({
            fromFlightNumber: delayedFlight.flightNumber,
            toFlightNumber: null,
            airline: delayedFlight.airline,
            airlineCode,
            mealsReassigned: 0,
            bottlesReassigned: 0,
            status: "no_flight",
            reason: `No same-airline flight available within 6 hours; mark as expiring`,
          });
          reassignments.push(reassignment);
        }
      }

      res.json({ reassignments, processed: delayedFlights.length });
    } catch (error: any) {
      res.status(500).json({ error: error.message || "Failed to process reassignments" });
    }
  });

  // Demo mode routes
  app.post("/api/demo/load", async (_req, res) => {
    try {
      await storage.loadDemoData();
      res.json({ success: true, message: "Demo data loaded successfully" });
    } catch (error: any) {
      res.status(500).json({ error: error.message || "Failed to load demo data" });
    }
  });

  app.post("/api/demo/clear", async (_req, res) => {
    try {
      await storage.clearAllData();
      res.json({ success: true, message: "All data cleared successfully" });
    } catch (error: any) {
      res.status(500).json({ error: error.message || "Failed to clear data" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

client/src/pages/replanning.tsx.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Calendar, ArrowRight, Package, Clock, RefreshCw, CheckCircle2, XCircle, AlertCircle } from "lucide-react";
import type { Flight, Reassignment } from "@shared/schema";
import { format } from "date-fns";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";

export default function Replanning() {
  const { toast } = useToast();

  const { data: flights, isLoading } = useQuery<Flight[]>({
    queryKey: ["/api/flights"],
  });

  const { data: reassignments, isLoading: reassignmentsLoading } = useQuery<Reassignment[]>({
    queryKey: ["/api/reassignments"],
    refetchInterval: 5000,
  });

  const processReassignmentsMutation = useMutation({
    mutationFn: async () => {
      const res = await apiRequest("POST", "/api/reassignments/process");
      return res as { reassignments: Reassignment[]; processed: number };
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ["/api/reassignments"] });
      toast({
        title: "Reasignaciones procesadas",
        description: `Se procesaron ${data.processed} vuelos afectados`,
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "No se pudieron procesar las reasignaciones",
        variant: "destructive",
      });
    },
  });

  const delayedFlights = flights?.filter((f) => f.status === "delayed" || f.status === "cancelled") || [];
  const scheduledFlights = flights?.filter((f) => f.status === "scheduled") || [];

  const getStatusIcon = (status: string) => {
    switch (status) {
      case "success":
        return <CheckCircle2 className="h-4 w-4 text-green-600 dark:text-green-400" />;
      case "pending":
        return <AlertCircle className="h-4 w-4 text-yellow-600 dark:text-yellow-400" />;
      case "no_flight":
        return <XCircle className="h-4 w-4 text-red-600 dark:text-red-400" />;
      default:
        return null;
    }
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case "success":
        return <Badge variant="default" className="gap-1 bg-green-600 hover:bg-green-700"> Exitoso</Badge>;
      case "pending":
        return <Badge variant="secondary" className="gap-1"> Pendiente</Badge>;
      case "no_flight":
        return <Badge variant="destructive" className="gap-1"> Sin Vuelo</Badge>;
      default:
        return null;
    }
  };

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-3xl font-bold text-foreground mb-2">Flight Replanning</h1>
        <p className="text-sm text-muted-foreground">Reassign resources when flight schedules change</p>
      </div>

      {isLoading ? (
        <div className="flex items-center justify-center h-64">
          <div className="text-center">
            <div className="h-8 w-8 border-4 border-primary border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
            <p className="text-sm text-muted-foreground">Loading replanning data...</p>
          </div>
        </div>
      ) : (
        <div className="space-y-6">
          {delayedFlights.length > 0 ? (
            <>
              <Card>
                <CardHeader>
                  <CardTitle className="text-lg font-semibold">Delayed Flights Requiring Action</CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  {delayedFlights.map((flight) => (
                    <div
                      key={flight.id}
                      className="p-4 rounded-md border bg-card space-y-3"
                      data-testid={`delayed-flight-${flight.id}`}
                    >
                      <div className="flex items-center justify-between">
                        <div>
                          <h3 className="font-mono text-lg font-bold">{flight.flightNumber}</h3>
                          <p className="text-sm text-muted-foreground">
                            {flight.airline}  {flight.destination}
                          </p>
                        </div>
                        <Badge variant="secondary" className="gap-1">
                          <Clock className="h-3 w-3" />
                          Delayed
                        </Badge>
                      </div>

                      <div className="flex items-center gap-2 text-sm text-muted-foreground">
                        <Calendar className="h-4 w-4" />
                        <span>Original: {format(new Date(flight.departureTime), "MMM dd, HH:mm")}</span>
                      </div>

                      <div className="p-3 rounded-md bg-muted/50 space-y-2">
                        <p className="text-sm font-medium">Available Actions:</p>
                        <ul className="space-y-1 text-sm text-muted-foreground">
                          <li className="flex items-center gap-2">
                            <Package className="h-3 w-3" />
                            <span>{flight.plannedMeals} meals can be reassigned to compatible flights</span>
                          </li>
                          <li className="flex items-center gap-2">
                            <Package className="h-3 w-3" />
                            <span>{flight.plannedBottles} bottles available for reallocation</span>
                          </li>
                        </ul>
                      </div>

                      {scheduledFlights.length > 0 && (
                        <div className="pt-2 border-t">
                          <p className="text-sm font-medium mb-2">Suggested Reassignments:</p>
                          <div className="space-y-2">
                            {scheduledFlights.slice(0, 2).map((targetFlight) => (
                              <div
                                key={targetFlight.id}
                                className="flex items-center justify-between p-2 rounded-md bg-background/50"
                              >
                                <div className="flex items-center gap-2 text-sm">
                                  <ArrowRight className="h-4 w-4 text-muted-foreground" />
                                  <span className="font-mono font-medium">{targetFlight.flightNumber}</span>
                                  <span className="text-muted-foreground">
                                    {format(new Date(targetFlight.departureTime), "HH:mm")}
                                  </span>
                                </div>
                                <Button size="sm" variant="outline" data-testid={`button-reassign-${targetFlight.id}`}>
                                  Reassign
                                </Button>
                              </div>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  ))}
                </CardContent>
              </Card>

              {/* Process Reassignments Button */}
              {delayedFlights.length > 0 && (
                <Card>
                  <CardContent className="pt-6">
                    <Button
                      onClick={() => processReassignmentsMutation.mutate()}
                      disabled={processReassignmentsMutation.isPending}
                      className="w-full gap-2"
                      data-testid="button-process-reassignments"
                    >
                      <RefreshCw className={`h-4 w-4 ${processReassignmentsMutation.isPending ? 'animate-spin' : ''}`} />
                      {processReassignmentsMutation.isPending ? "Procesando..." : "Procesar Reasignaciones"}
                    </Button>
                  </CardContent>
                </Card>
              )}
            </>
          ) : (
            <Card>
              <CardContent className="flex flex-col items-center justify-center py-16">
                <Calendar className="h-16 w-16 text-muted-foreground mb-4" />
                <h3 className="text-lg font-semibold mb-2">No Replanning Needed</h3>
                <p className="text-sm text-muted-foreground text-center max-w-md">
                  All flights are on schedule. Delayed flights will appear here for resource reassignment.
                </p>
              </CardContent>
            </Card>
          )}

          {/* Reassignment Logs */}
          {reassignments && reassignments.length > 0 && (
            <Card>
              <CardHeader>
                <CardTitle className="text-lg font-semibold">Historial de Reasignaciones</CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                {reassignments.map((reassignment) => (
                  <div
                    key={reassignment.id}
                    className="p-4 rounded-md border bg-card space-y-2"
                    data-testid={`reassignment-${reassignment.id}`}
                  >
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-2">
                        {getStatusIcon(reassignment.status)}
                        <span className="font-mono font-medium">{reassignment.fromFlightNumber}</span>
                        {reassignment.toFlightNumber && (
                          <>
                            <ArrowRight className="h-4 w-4 text-muted-foreground" />
                            <span className="font-mono font-medium">{reassignment.toFlightNumber}</span>
                          </>
                        )}
                        <span className="text-sm text-muted-foreground">({reassignment.airline})</span>
                      </div>
                      {getStatusBadge(reassignment.status)}
                    </div>

                    <p className="text-sm text-muted-foreground">{reassignment.reason}</p>

                    {reassignment.status === "success" && (
                      <div className="flex items-center gap-4 text-sm text-muted-foreground">
                        <span>{reassignment.mealsReassigned} comidas</span>
                        <span>{reassignment.bottlesReassigned} botellas</span>
                      </div>
                    )}

                    <p className="text-xs text-muted-foreground">
                      {format(new Date(reassignment.timestamp), "MMM dd, yyyy HH:mm")}
                    </p>
                  </div>
                ))}
              </CardContent>
            </Card>
          )}

          {/* Replanning Guide */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg font-semibold">Reglas de Reasignacin</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="space-y-3">
                <div className="p-4 rounded-md bg-muted/30 border">
                  <h4 className="text-sm font-medium mb-2">Restriccin de Aerolnea</h4>
                  <p className="text-sm text-muted-foreground">
                    Los alimentos frescos solo se reasignan entre vuelos de la <strong>misma aerolnea</strong>. 
                    Nunca se reasignan alimentos entre aerolneas diferentes (e.g., comidas de AM245 no pueden ir a DL321).
                  </p>
                </div>

                <div className="p-4 rounded-md bg-muted/30 border">
                  <h4 className="text-sm font-medium mb-2">Reglas de Prioridad</h4>
                  <ul className="text-sm text-muted-foreground space-y-1 mt-2">
                    <li> Solo vuelos de la misma aerolnea (cdigo IATA)</li>
                    <li> Vuelos con salida dentro de 6 horas</li>
                    <li> Prioridad al vuelo con salida ms cercana</li>
                    <li> Si no hay vuelo compatible  marcar como "Expirando"</li>
                  </ul>
                </div>

                <div className="p-4 rounded-md bg-muted/30 border">
                  <h4 className="text-sm font-medium mb-2">Indicadores de Estado</h4>
                  <ul className="text-sm text-muted-foreground space-y-1 mt-2">
                    <li> <strong>Exitoso</strong> - Reasignacin completada (misma aerolnea)</li>
                    <li> <strong>Pendiente</strong> - Esperando confirmacin</li>
                    <li> <strong>Sin Vuelo</strong> - No hay vuelo compatible disponible</li>
                  </ul>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      )}
    </div>
  );
}

