import type { WeatherData } from "@shared/schema";

const OPENWEATHERMAP_API_KEY = process.env.OPENWEATHERMAP_API_KEY;

// Extract airport code from destination string (e.g., "New York (JFK)" -> "JFK")
function extractAirportCode(destination: string): string {
  const match = destination.match(/\(([A-Z]{3})\)/);
  return match ? match[1] : destination;
}

// Airport coordinates for major airports worldwide
const airportCoordinates: Record<string, { lat: number; lon: number }> = {
  // USA
  JFK: { lat: 40.6413, lon: -73.7781 },
  LAX: { lat: 33.9416, lon: -118.4085 },
  ORD: { lat: 41.9742, lon: -87.9073 },
  ATL: { lat: 33.6407, lon: -84.4277 },
  DFW: { lat: 32.8998, lon: -97.0403 },
  MIA: { lat: 25.7959, lon: -80.2870 },
  SFO: { lat: 37.6213, lon: -122.3790 },
  SEA: { lat: 47.4502, lon: -122.3088 },
  LAS: { lat: 36.0840, lon: -115.1537 },

  // Europe
  LHR: { lat: 51.4700, lon: -0.4543 },
  CDG: { lat: 49.0097, lon: 2.5479 },
  AMS: { lat: 52.3105, lon: 4.7683 },
  FRA: { lat: 50.0379, lon: 8.5622 },
  MAD: { lat: 40.4719, lon: -3.5626 },
  BCN: { lat: 41.2974, lon: 2.0833 },

  // Mexico
  MEX: { lat: 19.4363, lon: -99.0721 },
  CUN: { lat: 21.0365, lon: -86.8771 },
  GDL: { lat: 20.5218, lon: -103.3119 },
  MTY: { lat: 25.7785, lon: -100.1069 },

  // South America
  GRU: { lat: -23.4356, lon: -46.4731 },
  EZE: { lat: -34.8222, lon: -58.5358 },

  // Asia
  NRT: { lat: 35.7720, lon: 140.3929 },
  HKG: { lat: 22.3080, lon: 113.9185 },
  SIN: { lat: 1.3644, lon: 103.9915 },
  DXB: { lat: 25.2532, lon: 55.3657 },
};

export async function getWeatherData(destination: string, dateTime: Date): Promise<WeatherData> {
  const airportCode = extractAirportCode(destination);
  const coords = airportCoordinates[airportCode];

  // If no API key or unknown airport, use mock data
  if (!OPENWEATHERMAP_API_KEY || !coords) {
    return getMockWeatherData();
  }

  try {
    const response = await fetch(
      `https://api.openweathermap.org/data/2.5/weather?lat=${coords.lat}&lon=${coords.lon}&appid=${OPENWEATHERMAP_API_KEY}&units=metric`
    );

    if (!response.ok) {
      console.warn("Weather API request failed, using mock data");
      return getMockWeatherData();
    }

    const data = await response.json();

    return {
      temperature: Math.round(data.main.temp),
      windSpeed: Math.round(data.wind.speed * 3.6), // m/s to km/h
      precipitation: data.rain?.["1h"] ? Math.round(data.rain["1h"] * 100) : 0,
      visibility: Math.round(data.visibility / 1000), // meters to km
      conditions: data.weather[0]?.main || "Clear",
    };
  } catch (error) {
    console.warn("Weather API error, using mock data:", error);
    return getMockWeatherData();
  }
}

function getMockWeatherData(): WeatherData {
  const randomFactor = Math.random();
  const conditions = ["Clear", "Partly Cloudy", "Cloudy", "Rain", "Thunderstorm", "Fog"];
  const conditionIndex = Math.floor(randomFactor * conditions.length);

  return {
    temperature: 15 + Math.floor(randomFactor * 20),
    windSpeed: Math.floor(randomFactor * 50),
    precipitation: Math.floor(randomFactor * 100),
    visibility: 5 + Math.floor(randomFactor * 5),
    conditions: conditions[conditionIndex],
  };
}

export function calculateFlightReliability(weather: WeatherData): number {
  let reliability = 100;

  // Reduce reliability based on adverse conditions
  if (weather.conditions === "Thunderstorm") {
    reliability -= 40;
  } else if (weather.conditions === "Fog") {
    reliability -= 30;
  } else if (weather.conditions === "Rain") {
    reliability -= 20;
  } else if (weather.conditions === "Cloudy") {
    reliability -= 10;
  }

  // Wind speed impact
  if (weather.windSpeed > 40) {
    reliability -= 25;
  } else if (weather.windSpeed > 30) {
    reliability -= 15;
  } else if (weather.windSpeed > 20) {
    reliability -= 10;
  }

  // Precipitation impact
  if (weather.precipitation > 70) {
    reliability -= 20;
  } else if (weather.precipitation > 40) {
    reliability -= 10;
  }

  // Visibility impact
  if (weather.visibility < 3) {
    reliability -= 25;
  } else if (weather.visibility < 5) {
    reliability -= 15;
  }

  return Math.max(0, Math.min(100, reliability));
}

export function getReliabilityRecommendation(reliability: number): string {
  if (reliability >= 80) {
    return "Excellent flight conditions. Proceed with full meal preparation as planned.";
  } else if (reliability >= 70) {
    return "Good flight conditions with minor concerns. Maintain standard operations.";
  } else if (reliability >= 50) {
    return "Moderate concerns due to weather. Consider reducing fresh food load by 20%.";
  } else {
    return "Low reliability due to adverse weather. Recommend reducing fresh food load by 30-40% to minimize waste.";
  }
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, integer, real, timestamp, jsonb } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Flight statuses
export const FlightStatus = {
  SCHEDULED: "scheduled",
  DELAYED: "delayed",
  CANCELLED: "cancelled",
} as const;

export type FlightStatus = typeof FlightStatus[keyof typeof FlightStatus];

// Bottle actions
export const BottleAction = {
  REUSE: "reuse",
  COMBINE: "combine",
  DISCARD: "discard",
} as const;

export type BottleAction = typeof BottleAction[keyof typeof BottleAction];

// Flights table
export const flights = pgTable("flights", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  flightNumber: text("flight_number").notNull(),
  departureTime: timestamp("departure_time").notNull(),
  destination: text("destination").notNull(),
  airline: text("airline").notNull(),
  status: text("status").notNull().$type<FlightStatus>(),
  reliability: real("reliability"), // percentage 0-100
  plannedMeals: integer("planned_meals").notNull().default(0),
  plannedBottles: integer("planned_bottles").notNull().default(0),
  actualMeals: integer("actual_meals"),
  actualBottles: integer("actual_bottles"),
});

export const insertFlightSchema = createInsertSchema(flights).omit({
  id: true,
}).extend({
  departureTime: z.coerce.date(),
});

export type InsertFlight = z.infer<typeof insertFlightSchema>;
export type Flight = typeof flights.$inferSelect;

// Airline rules for bottle handling
export const airlineRules = pgTable("airline_rules", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  airline: text("airline").notNull().unique(),
  reuseThreshold: integer("reuse_threshold").notNull().default(70), // >70% reuse
  combineThreshold: integer("combine_threshold").notNull().default(40), // 40-70% combine
  // <40% discard (implicit)
});

export const insertAirlineRuleSchema = createInsertSchema(airlineRules).omit({
  id: true,
});

export type InsertAirlineRule = z.infer<typeof insertAirlineRuleSchema>;
export type AirlineRule = typeof airlineRules.$inferSelect;

// Bottle analysis records
export const bottleAnalyses = pgTable("bottle_analyses", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  flightId: varchar("flight_id").references(() => flights.id),
  imageData: text("image_data"), // base64 encoded
  bottleType: text("bottle_type"),
  fillLevel: integer("fill_level"), // percentage 0-100
  recommendedAction: text("recommended_action").$type<BottleAction>(),
  aiAnalysis: text("ai_analysis"), // raw AI response
  timestamp: timestamp("timestamp").notNull().defaultNow(),
});

export const insertBottleAnalysisSchema = createInsertSchema(bottleAnalyses).omit({
  id: true,
  timestamp: true,
});

export type InsertBottleAnalysis = z.infer<typeof insertBottleAnalysisSchema>;
export type BottleAnalysis = typeof bottleAnalyses.$inferSelect;

// Trolley verifications
export const trolleyVerifications = pgTable("trolley_verifications", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  flightId: varchar("flight_id").references(() => flights.id),
  imageData: text("image_data"), // base64 encoded
  goldenLayoutName: text("golden_layout_name"),
  hasErrors: integer("has_errors").notNull().default(0), // 0 or 1 (boolean)
  errors: jsonb("errors").$type<string[]>(), // array of error descriptions
  aiAnalysis: text("ai_analysis"), // raw AI response
  timestamp: timestamp("timestamp").notNull().defaultNow(),
});

export const insertTrolleyVerificationSchema = createInsertSchema(trolleyVerifications).omit({
  id: true,
  timestamp: true,
});

export type InsertTrolleyVerification = z.infer<typeof insertTrolleyVerificationSchema>;
export type TrolleyVerification = typeof trolleyVerifications.$inferSelect;

// Weather data for flight reliability
export interface WeatherData {
  temperature: number;
  windSpeed: number;
  precipitation: number;
  visibility: number;
  conditions: string;
}

// Dashboard metrics
export interface DashboardMetrics {
  totalFlights: number;
  averageReliability: number;
  employeeEfficiency: number;
  foodSaved: number;
  bottlesReused: number;
  bottlesCombined: number;
  bottlesDiscarded: number;
  trolleyErrorRate: number;
}

// Employee metrics
export interface EmployeeMetric {
  employeeName: string;
  avgPrepTime: number;
  errorRate: number;
  complianceRate: number;
  trolleysProcessed: number;
}

// Chart data for trends
export interface TrendData {
  date: string;
  value: number;
  label?: string;
}

// API response types
export interface BottleAnalysisResult {
  bottleType: string;
  fillLevel: number;
  recommendedAction: BottleAction;
  analysis: string;
}

export interface TrolleyVerificationResult {
  hasErrors: boolean;
  errors: string[];
  analysis: string;
}

export interface FlightReliabilityResult {
  reliability: number;
  weather: WeatherData;
  recommendation: string;
}

// Reassignment statuses
export const ReassignmentStatus = {
  SUCCESS: "success",
  PENDING: "pending",
  NO_FLIGHT: "no_flight",
} as const;

export type ReassignmentStatus = typeof ReassignmentStatus[keyof typeof ReassignmentStatus];

// Reassignment logs table
export const reassignments = pgTable("reassignments", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  fromFlightNumber: text("from_flight_number").notNull(),
  toFlightNumber: text("to_flight_number"),
  airline: text("airline").notNull(),
  airlineCode: text("airline_code").notNull(),
  mealsReassigned: integer("meals_reassigned").notNull().default(0),
  bottlesReassigned: integer("bottles_reassigned").notNull().default(0),
  status: text("status").notNull().$type<ReassignmentStatus>(),
  reason: text("reason"),
  timestamp: timestamp("timestamp").notNull().defaultNow(),
});

export const insertReassignmentSchema = createInsertSchema(reassignments).omit({
  id: true,
  timestamp: true,
});

export type InsertReassignment = z.infer<typeof insertReassignmentSchema>;
export type Reassignment = typeof reassignments.$inferSelect;

// Order statuses for customer orders workflow
export const OrderStatus = {
  PENDING: "pending",
  IN_VERIFICATION: "in_verification",
  COMPLETED: "completed",
  CANCELLED: "cancelled",
} as const;

export type OrderStatus = typeof OrderStatus[keyof typeof OrderStatus];

// Customer orders table
export const orders = pgTable("orders", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  flightNumber: text("flight_number").notNull(),
  airline: text("airline").notNull(),
  departureTime: timestamp("departure_time").notNull(),
  destination: text("destination").notNull(),
  mealsRequested: integer("meals_requested").notNull().default(0),
  snacksRequested: integer("snacks_requested").notNull().default(0),
  beveragesRequested: integer("beverages_requested").notNull().default(0),
  status: text("status").notNull().$type<OrderStatus>().default("pending"),
  timestamp: timestamp("timestamp").notNull().defaultNow(),
});

export const insertOrderSchema = createInsertSchema(orders).omit({
  id: true,
  timestamp: true,
}).extend({
  departureTime: z.coerce.date(),
});

export type InsertOrder = z.infer<typeof insertOrderSchema>;
export type Order = typeof orders.$inferSelect;

